import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Login from "./Login/Login";
import Signup from "./Login/Signup";
import Production from "./Production/Production";
import Electrical from "./Electrical/Electrical";
import "./App.css"; // Custom styling

const App = () => {
    const [isAuthenticated, setIsAuthenticated] = useState(false);

    useEffect(() => {
        // Check for auth token
        const authToken = localStorage.getItem("authToken");
        setIsAuthenticated(!!authToken);
    }, []);

    const handleAuth = (token) => {
        localStorage.setItem("authToken", token);
        setIsAuthenticated(true);
    };

    return (
        <Router>
            <div className="app-container">
                <Routes>
                    <Route path="/" element={<Login onAuth={handleAuth} />} />
                    <Route path="/signup" element={<Signup />} />

                    {/* Protected Routes */}
                    <Route path="/production" element={isAuthenticated ? <Production /> : <Navigate to="/" />} />
                    <Route path="/electrical" element={isAuthenticated ? <Electrical /> : <Navigate to="/" />} />

                    {/* Redirect if path not found */}
                    <Route path="*" element={<Navigate to="/" />} />
                </Routes>
            </div>
        </Router>
    );
};

export default App;
