import React, { useState, useEffect } from "react";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
} from "recharts";
import "bootstrap/dist/css/bootstrap.min.css";
import "./Production.css";

const departments = [
  "MIXING",
  "BR_CDG",
  "PREDRG",
  "LH15",
  "COMBER",
  "DRG",
  "SMX",
  "SPG",
  "ACWDG",
  "PACKBAGS",
];

const Production = () => {
  const [leftDate, setLeftDate] = useState("");
  const [rightDate, setRightDate] = useState("");
  const [leftGraphData, setLeftGraphData] = useState([]);
  const [rightGraphData, setRightGraphData] = useState([]);
  const [graphData, setGraphData] = useState([]);
  const [data, setData] = useState({});
  const [cumulative, setCumulative] = useState([]);

  useEffect(() => {
    const initialData = departments.reduce((acc, dept) => {
      acc[dept] = { ondate_prod: 0, ondate_hands: 0 };
      return acc;
    }, {});
    setData(initialData);
  }, []);

  useEffect(() => {
    const fetchGraphData = async (date, setGraphData) => {
      if (date) {
        try {
          const res = await fetch(`http://localhost:5000/production/selected-date/${date}`);
          const result = await res.json();

          const formattedData = departments.map((dept) => ({
            name: dept,
            Production: result[dept]?.ondate_prod || 0,
            Hands: result[dept]?.ondate_hands || 0,
          }));

          setGraphData(formattedData);
        } catch (err) {
          console.error("Error fetching graph data:", err);
        }
      }
    };

    fetchGraphData(leftDate, setLeftGraphData);
    fetchGraphData(rightDate, setRightGraphData);

    if (rightDate) {
      const month = rightDate.slice(0, 7);
      fetch(`http://localhost:5000/production/cumulative/${month}`)
        .then((res) => res.json())
        .then((data) => {
          const formatted = departments.map((dept) => ({
            date: month,
            department: dept,
            prod: data[dept]?.prod || 0,
            hands: data[dept]?.hands || 0,
          }));
          setCumulative(formatted);
          setGraphData(formatted); // Set for chart too
        })
        .catch((err) => console.error("Error fetching cumulative data:", err));
    }
  }, [leftDate, rightDate]);

  return (
    <div className="container-fluid p-5">
      <div className="row mb-4">
        {/* Graph Visualization */}
        <div className="col-12 mb-5">
          <h3 className="text-primary text-center">Graph Visualization</h3>
          <ResponsiveContainer width="100%" height={400}>
            <LineChart data={graphData}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="department" />
              <YAxis />
              <Tooltip />
              <Legend />
              <Line type="monotone" dataKey="prod" stroke="#8884d8" name="Production" />
              <Line type="monotone" dataKey="hands" stroke="#82ca9d" name="Hands" />
            </LineChart>
          </ResponsiveContainer>
        </div>

        {/* Date Selectors */}
        <div className="col-md-6">
          <label className="form-label fw-bold">Select Left Date:</label>
          <input
            type="date"
            value={leftDate}
            onChange={(e) => setLeftDate(e.target.value)}
            className="form-control mb-3"
          />
        </div>
        <div className="col-md-6">
          <label className="form-label fw-bold">Select Right (Cumulative) Date:</label>
          <input
            type="date"
            value={rightDate}
            onChange={(e) => setRightDate(e.target.value)}
            className="form-control mb-3"
          />
        </div>

        {/* Cumulative Data Table */}
        <div className="col-12 mt-4">
          <h4 className="text-center text-success">Cumulative Monthly Data</h4>
          <table className="table table-bordered mt-3">
            <thead className="table-dark">
              <tr>
                <th>Date</th>
                <th>Department</th>
                <th>Production</th>
                <th>Hands</th>
              </tr>
            </thead>
            <tbody>
              {cumulative.map((item, index) => (
                <tr key={index}>
                  <td>{item.date}</td>
                  <td>{item.department}</td>
                  <td>{item.prod}</td>
                  <td>{item.hands}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default Production;
