import React from "react";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import Production from "./Production/Production";
import Electrical from "./Electrical/Electrical";
import "./App.css"; // Add some basic styling

const App = () => {
  return (
    <Router>
      <div className="app-container">
        <nav className="navbar">
          <h1>Production Management</h1>
          <div className="nav-links">
            <Link to="/">Production</Link>
            <Link to="/electrical">Electrical Maintenance</Link>
          </div>
        </nav>

        <Routes>
          {/* Production page */}
          <Route
            path="/"
            element={
              <div className="page">
                <Production />
                <div className="button-container">
                  <Link to="/electrical" className="btn-electrical">
                    Go to Electrical Maintenance ➡️
                  </Link>
                </div>
              </div>
            }
          />

          {/* Electrical page */}
          <Route
            path="/electrical"
            element={
              <div className="page">
                <Electrical />
                <div className="button-container">
                  <Link to="/" className="btn-production">
                    ⬅️ Back to Production
                  </Link>
                </div>
              </div>
            }
          />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
