import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Login from "./Login/Login.js";
import Signup from "./Login/Signup";
import Production from "./Production/Production";
import Electrical from "./Electrical/Electrical";
import "./App.css"; // Add some basic styling

const App = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  // Mock authentication handler
  const handleAuth = (status) => {
    setIsAuthenticated(status);
  };

  return (
    <Router>
      <div className="app-container">
        <Routes>
          {/* Authentication Routes */}
          <Route path="/" element={<Login onAuth={() => handleAuth(true)} />} />
          <Route path="/signup" element={<Signup />} />

          {/* Protected Routes */}
          <Route 
            path="/production" 
            element={isAuthenticated ? <Production /> : <Navigate to="/" />} 
          />
          <Route 
            path="/electrical" 
            element={isAuthenticated ? <Electrical /> : <Navigate to="/" />} 
          />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
