import React, { useState, useEffect } from "react";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
} from "recharts";
import "bootstrap/dist/css/bootstrap.min.css";
import "./Production.css"; // Custom CSS for styling
import Swal from "sweetalert2"; // ✅ For popup messages

const departments = [
  "MIXING",
  "BR_CDG",
  "PREDRG",
  "LH15",
  "COMBER",
  "DRG",
  "SMX",
  "SPG",
  "ACWDG",
  "PACKBAGS",
];

const Production = () => {
  const [leftDate, setLeftDate] = useState("");
  const [rightDate, setRightDate] = useState("");
  const [leftGraphData, setLeftGraphData] = useState([]);
  const [rightGraphData, setRightGraphData] = useState([]);
  const [data, setData] = useState({});
  const [cumulative, setCumulative] = useState({});

  useEffect(() => {
    const initialData = departments.reduce((acc, dept) => {
      acc[dept] = { ondate_prod: 0, ondate_hands: 0 };
      return acc;
    }, {});
    setData(initialData);
  }, []);

  const fetchGraphData = (date, setGraphData) => {
    if (date) {
      fetch(`http://localhost:5000/production/selected-date/${date}`)
        .then((res) => res.json())
        .then((data) => {
          setGraphData(
            departments.map((dept) => ({
              name: dept,
              Production: data[dept]?.ondate_prod || 0,
              Hands: data[dept]?.ondate_hands || 0,
            }))
          );
        })
        .catch((err) => console.error("Error fetching data:", err));
    }
  };

  const fetchCumulativeData = () => {
    if (rightDate) {
      const month = rightDate.slice(0, 7);
      fetch(`http://localhost:5000/cumulative/${month}`)
        .then((res) => res.json())
        .then((data) => setCumulative(data))
        .catch((err) => console.error("Error fetching cumulative data:", err));
    }
  };

  useEffect(() => {
    fetchGraphData(leftDate, setLeftGraphData);
    fetchGraphData(rightDate, setRightGraphData);
    fetchCumulativeData();
  }, [leftDate, rightDate]);

  const handleChange = (dept, field, value) => {
    setData((prev) => ({
      ...prev,
      [dept]: { ...prev[dept], [field]: Math.max(0, Number(value)) },
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const newData = { date: leftDate, ...data };

    try {
      const response = await fetch("http://localhost:5000/add", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(newData),
      });

      const result = await response.json();

      if (response.ok) {
        Swal.fire({
          title: "Success!",
          text: "Data successfully entered!",
          icon: "success",
          timer: 2000,
        });

        fetchCumulativeData();
        setLeftDate("");
      } else {
        throw new Error(result.message);
      }
    } catch (err) {
      console.error("Error submitting data:", err);
    }
  };

  return (
    <div className="container-fluid p-5">
      <div className="row mb-5">
        <div className="col-md-6">
          <h3 className="text-primary font-weight-bold">Production Entry</h3>
          <form onSubmit={handleSubmit} className="bg-light p-4 rounded shadow-lg">
            <div className="mb-3">
              <label>Select Date:</label>
              <input
                type="date"
                value={leftDate}
                onChange={(e) => setLeftDate(e.target.value)}
                className="form-control"
                required
              />
            </div>

            {departments.map((dept) => (
              <div key={dept} className="mb-3">
                <h5>{dept}</h5>
                <input
                  type="number"
                  value={data[dept]?.ondate_prod || 0}
                  onChange={(e) => handleChange(dept, "ondate_prod", e.target.value)}
                  className="form-control mb-2"
                  placeholder="Production"
                  required
                />
                <input
                  type="number"
                  value={data[dept]?.ondate_hands || 0}
                  onChange={(e) => handleChange(dept, "ondate_hands", e.target.value)}
                  className="form-control"
                  placeholder="Hands"
                  required
                />
              </div>
            ))}
            <button type="submit" className="btn btn-success mt-3">Submit</button>
          </form>
        </div>

        <div className="col-md-6">
          <h3 className="text-primary">Cumulative Data</h3>
          <table className="table table-bordered mt-3">
            <thead className="table-dark">
              <tr>
                <th>Department</th>
                <th>PRODN</th>
                <th>HANDS</th>
              </tr>
            </thead>
            <tbody>
              {departments.map((dept) => (
                <tr key={dept}>
                  <td>{dept}</td>
                  <td>{cumulative[dept]?.prod || 0}</td>
                  <td>{cumulative[dept]?.hands || 0}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default Production;
