import React, { useState } from 'react';
import {
  LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer
} from 'recharts';
import 'bootstrap/dist/css/bootstrap.min.css';

const App = () => {
  const [entries, setEntries] = useState([]);
  const [type, setType] = useState('');
  const [date, setDate] = useState('');

  const sections = ['Top Apron', 'Middle Apron', 'Bottom Apron'];

  // ✅ Function to calculate Life in Days, Months, and Next Schedule
  const calculateLife = (date) => {
    const startDate = new Date(date);
    const endDate = new Date('2025-01-14');

    // Ensure no negative values
    const lifeInDays = Math.max(0, Math.floor((endDate - startDate) / (1000 * 60 * 60 * 24)));
    const lifeInMonths = Math.max(0, (lifeInDays / 30.44).toFixed(2));

    const nextDate = new Date(endDate);
    nextDate.setDate(nextDate.getDate() + 60);  // Next schedule in 2 months

    return {
      lifeInDays,
      lifeInMonths,
      nextDate: nextDate.toISOString().split('T')[0],
    };
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!type || !date) return;

    const { lifeInDays, lifeInMonths, nextDate } = calculateLife(date);

    const newEntries = sections.map((section) => ({
      section,
      type,
      date,
      lifeInDays,
      lifeInMonths,
      nextDate,
    }));

    setEntries([...entries, ...newEntries]);
    setType('');
    setDate('');
  };

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-4">Spinning Mills Tracker</h1>

      {/* Form Section */}
      <form onSubmit={handleSubmit} className="mb-5">
        <div className="row g-3">
          <div className="col-md-4">
            <label>Type</label>
            <input
              type="text"
              className="form-control"
              value={type}
              onChange={(e) => setType(e.target.value)}
              required
            />
          </div>

          <div className="col-md-4">
            <label>Date</label>
            <input
              type="date"
              className="form-control"
              value={date}
              onChange={(e) => setDate(e.target.value)}
              required
            />
          </div>

          <div className="col-md-4 d-flex align-items-end">
            <button type="submit" className="btn btn-primary w-100">Add Entry</button>
          </div>
        </div>
      </form>

      {/* Table Section */}
      <h3 className="text-center">All Sections Data</h3>
      <table className="table table-bordered table-striped mt-4">
        <thead className="table-dark">
          <tr>
            <th>Section</th>
            <th>Type</th>
            <th>Date</th>
            <th>Life in Days</th>
            <th>Life in Months</th>
            <th>Next Schedule</th>
          </tr>
        </thead>
        <tbody>
          {entries.map((entry, index) => (
            <tr key={index}>
              <td>{entry.section}</td>
              <td>{entry.type}</td>
              <td>{entry.date}</td>
              <td>{entry.lifeInDays}</td>
              <td>{entry.lifeInMonths}</td>
              <td>{entry.nextDate}</td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Graph Visualization */}
      <div className="mt-5">
        <h3 className="text-center">Graph Visualization</h3>
        <ResponsiveContainer width="100%" height={400}>
          <LineChart data={entries}>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="date" />
            <YAxis />
            <Tooltip />
            <Legend />
            <Line type="monotone" dataKey="lifeInDays" stroke="#8884d8" name="Life in Days" />
            <Line type="monotone" dataKey="lifeInMonths" stroke="#82ca9d" name="Life in Months" />
          </LineChart>
        </ResponsiveContainer>
      </div>
    </div>
  );
};

export default App;
