import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate, useNavigate } from "react-router-dom";
import Login from "./Login/Login.js";
import Signup from "./Login/Signup.js";
import Production from "./Production/Production";
import Electrical from "./Electrical/Electrical";
import "./App.css"; // Add some basic styling

const App = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  // Mock authentication handler
  const handleAuth = (status) => {
    setIsAuthenticated(status);
  };

  return (
    <Router>
      <div className="app-container">
        <Routes>
          {/* Authentication Routes */}
          <Route path="/" element={<Login onAuth={() => handleAuth(true)} />} />
          <Route path="/signup" element={<Signup />} />

          {/* Protected Routes */}
          <Route 
            path="/production" 
            element={isAuthenticated ? <Production /> : <Navigate to="/" />} 
          />
          <Route 
            path="/electrical" 
            element={isAuthenticated ? <Electrical /> : <Navigate to="/" />} 
          />

          {/* Navigation Buttons after Login */}
          <Route
            path="/dashboard"
            element={isAuthenticated ? <Dashboard /> : <Navigate to="/" />}
          />
        </Routes>
      </div>
    </Router>
  );
};

// ✅ Dashboard Component with Navigation Buttons
const Dashboard = () => {
  const navigate = useNavigate();

  return (
    <div style={{ textAlign: "center", marginTop: "100px" }}>
      <h1>Welcome to the Dashboard</h1>
      <p>Select a section to proceed:</p>
      <div style={{ display: "flex", justifyContent: "center", gap: "20px" }}>
        <button 
          onClick={() => navigate("/production")} 
          style={{ padding: "10px 20px", fontSize: "16px", cursor: "pointer" }}
        >
          Go to Production
        </button>
        <button 
          onClick={() => navigate("/electrical")} 
          style={{ padding: "10px 20px", fontSize: "16px", cursor: "pointer" }}
        >
          Go to Electrical
        </button>
      </div>
    </div>
  );
};

export default App;
