import React, { useState, useEffect } from "react";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
} from "recharts";
import "bootstrap/dist/css/bootstrap.min.css";
import Swal from "sweetalert2"; // ✅ For popup messages

const sections = ["Top Apron", "Middle Apron", "Bottom Apron"];

const App = () => {
  const [date, setDate] = useState("");
  const [cumulativeDate, setCumulativeDate] = useState("");
  const [data, setData] = useState({});
  const [graphData, setGraphData] = useState([]);
  const [cumulative, setCumulative] = useState([]);

  useEffect(() => {
    const initialData = sections.reduce((acc, section) => {
      acc[section] = { production: 0, hands: 0 };
      return acc;
    }, {});
    setData(initialData);
  }, []);

  const calculateLife = (date) => {
    const startDate = new Date(date);
    const endDate = new Date('2025-01-14');

    const lifeInDays = Math.max(0, Math.floor((endDate - startDate) / (1000 * 60 * 60 * 24)));
    const lifeInMonths = Math.max(0, (lifeInDays / 30.44).toFixed(2));

    const nextDate = new Date(endDate);
    nextDate.setDate(nextDate.getDate() + 60);

    return {
      lifeInDays,
      lifeInMonths,
      nextDate: nextDate.toISOString().split("T")[0],
    };
  };

  const handleChange = (section, field, value) => {
    setData((prev) => ({
      ...prev,
      [section]: { ...prev[section], [field]: Math.max(0, Number(value)) },
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const newData = sections.map((section) => {
      const { lifeInDays, lifeInMonths, nextDate } = calculateLife(date);
      return {
        date,
        section,
        production: data[section]?.production || 0,
        hands: data[section]?.hands || 0,
        lifeInDays,
        lifeInMonths,
        nextDate,
      };
    });

    setGraphData([...graphData, ...newData]);
    setDate("");

    Swal.fire({
      title: "Success!",
      text: "Data successfully entered!",
      icon: "success",
      timer: 2000,
    });
  };

  const handleCumulativeDateChange = (e) => {
    setCumulativeDate(e.target.value);
    const filteredData = graphData.filter((item) => item.date === e.target.value);
    setCumulative(filteredData);
  };

  return (
    <div className="container-fluid p-5">
      <div className="row mb-5">
        {/* ✅ Production Entry Form */}
        <div className="col-md-6">
          <h3 className="text-primary font-weight-bold">Production Entry</h3>
          <form onSubmit={handleSubmit} className="bg-light p-4 rounded shadow-lg">
            <div className="mb-3">
              <label>Select Date:</label>
              <input
                type="date"
                value={date}
                onChange={(e) => setDate(e.target.value)}
                className="form-control"
                required
              />
            </div>

            {sections.map((section) => (
              <div key={section} className="mb-3">
                <h5>{section}</h5>
                <input
                  type="number"
                  value={data[section]?.production || 0}
                  onChange={(e) => handleChange(section, "production", e.target.value)}
                  className="form-control mb-2"
                  placeholder="Production"
                  required
                />
                <input
                  type="number"
                  value={data[section]?.hands || 0}
                  onChange={(e) => handleChange(section, "hands", e.target.value)}
                  className="form-control"
                  placeholder="Hands"
                  required
                />
              </div>
            ))}
            <button type="submit" className="btn btn-success mt-3">Submit</button>
          </form>
        </div>

        {/* ✅ Cumulative Data Table with Date Picker */}
        <div className="col-md-6">
          <h3 className="text-primary">Cumulative Data</h3>

          <div className="mb-3">
            <label>Select Cumulative Date:</label>
            <input
              type="date"
              value={cumulativeDate}
              onChange={handleCumulativeDateChange}
              className="form-control"
              required
            />
          </div>

          <table className="table table-bordered mt-3">
            <thead className="table-dark">
              <tr>
                <th>Date</th>
                <th>Section</th>
                <th>Production</th>
                <th>Hands</th>
                <th>Life in Days</th>
                <th>Life in Months</th>
                <th>Next Schedule</th>
              </tr>
            </thead>
            <tbody>
              {cumulative.map((item, index) => (
                <tr key={index}>
                  <td>{item.date}</td>
                  <td>{item.section}</td>
                  <td>{item.production}</td>
                  <td>{item.hands}</td>
                  <td>{item.lifeInDays}</td>
                  <td>{item.lifeInMonths}</td>
                  <td>{item.nextDate}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {/* ✅ Graph Visualization */}
      <div className="row mt-5">
        <div className="col-12">
          <h3 className="text-primary text-center">Graph Visualization</h3>
          <ResponsiveContainer width="100%" height={400}>
            <LineChart data={graphData}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="section" />
              <YAxis />
              <Tooltip />
              <Legend />
              <Line type="monotone" dataKey="production" stroke="#8884d8" name="Production" />
              <Line type="monotone" dataKey="hands" stroke="#82ca9d" name="Hands" />
              <Line type="monotone" dataKey="lifeInDays" stroke="#ffc658" name="Life in Days" />
              <Line type="monotone" dataKey="lifeInMonths" stroke="#ff7300" name="Life in Months" />
            </LineChart>
          </ResponsiveContainer>
        </div>
      </div>
    </div>
  );
};

export default App;
