import React, { useState, useEffect } from "react";
import Swal from "sweetalert2";
import "bootstrap/dist/css/bootstrap.min.css";
import "./Electrical.css";

const sections = ["TOP_APRON", "MIDDLE_APRON", "BOTTOM_APRON"];

const ElectricalMaintenance = () => {
  const [date, setDate] = useState("");
  const [cumulativeData, setCumulativeData] = useState([]);
  const [formData, setFormData] = useState(
    sections.reduce((acc, section) => {
      acc[section] = { type: "", date: "" };
      return acc;
    }, {})
  );

  // ✅ Load cumulative data from backend on component mount
  useEffect(() => {
    const fetchCumulativeData = async () => {
      try {
        const response = await fetch("http://localhost:5000/cumulative");
        if (response.ok) {
          const data = await response.json();
          setCumulativeData(data);
        } else {
          console.error("Failed to fetch cumulative data");
        }
      } catch (error) {
        console.error("Error:", error);
      }
    };

    fetchCumulativeData();
  }, []);

  // ✅ Calculate Life & Next Schedule
  const calculateLife = (date) => {
    if (!date) return { days: 0, months: 0, nextSchedule: "-" };

    const currentDate = new Date();
    const inputDate = new Date(date);
    const diffTime = Math.abs(currentDate - inputDate);
    const lifeInDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
    const lifeInMonths = (lifeInDays / 30.44).toFixed(2);

    const nextSchedule = new Date(inputDate);
    nextSchedule.setFullYear(nextSchedule.getFullYear() + 2);
    const formattedNext = nextSchedule.toISOString().split("T")[0];

    return {
      days: lifeInDays,
      months: lifeInMonths,
      nextSchedule: formattedNext,
    };
  };

  // ✅ Handle Form Changes
  const handleChange = (section, field, value) => {
    setFormData((prev) => ({
      ...prev,
      [section]: { ...prev[section], [field]: value }
    }));
  };

  // ✅ Handle Form Submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    const newEntry = sections.map((section) => {
      const { type, date } = formData[section];
      const { days, months, nextSchedule } = calculateLife(date);

      return {
        section,
        type,
        date,
        lifeInDays: days,
        lifeInMonths: months,
        nextSchedule,
      };
    });

    const electricalData = {
      date,
      newEntry,
    };

    try {
      const response = await fetch("http://localhost:5000/add-electrical", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(electricalData),
      });

      if (response.ok) {
        const result = await response.json();

        // ✅ Refresh cumulative data after submission
        const updatedResponse = await fetch("http://localhost:5000/cumulative");
        if (updatedResponse.ok) {
          const updatedData = await updatedResponse.json();
          setCumulativeData(updatedData);
        }

        Swal.fire({
          title: "Success!",
          text: result.message,
          icon: "success",
          timer: 2000,
        });

        // ✅ Reset Form
        setFormData(sections.reduce((acc, section) => {
          acc[section] = { type: "", date: "" };
          return acc;
        }, {}));

      } else {
        Swal.fire({ title: "Error", text: "Failed to submit data", icon: "error" });
      }
    } catch (error) {
      Swal.fire({ title: "Error", text: "An error occurred", icon: "error" });
    }
  };

  return (
    <div className="container p-5">
      {/* ✅ Form Section */}
      <form onSubmit={handleSubmit} className="bg-light p-4 rounded shadow-lg mb-5">
        <div className="mb-3">
          <label className="form-label">Select Date:</label>
          <input
            type="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            className="form-control"
            required
          />
        </div>

        {sections.map((section) => (
          <div key={section} className="card mb-3 p-3 shadow-sm">
            <h5 className="text-success">{section}</h5>

            <div className="mb-3">
              <label className="form-label">Type:</label>
              <input
                type="text"
                placeholder={`${section} Type`}
                value={formData[section].type}
                onChange={(e) => handleChange(section, "type", e.target.value)}
                className="form-control"
                required
              />
            </div>

            <div className="mb-3">
              <label className="form-label">Date:</label>
              <input
                type="date"
                value={formData[section].date}
                onChange={(e) => handleChange(section, "date", e.target.value)}
                className="form-control"
                required
              />
            </div>
          </div>
        ))}

        <button type="submit" className="btn btn-primary w-100 mt-3">Submit</button>
      </form>

      {/* ✅ Cumulative Data Table */}
      <h2 className="text-center mb-4">Cumulative Electrical Data</h2>
      <div className="table-responsive">
        <table className="table table-striped table-bordered">
          <thead className="table-dark">
            <tr>
              <th>Section</th>
              <th>Type</th>
              <th>Life (Days)</th>
              <th>Life (Months)</th>
              <th>Next Schedule</th>
            </tr>
          </thead>
          <tbody>
            {cumulativeData && cumulativeData.length > 0 ? (
              cumulativeData.map((entry, index) => (
                <tr key={index}>
                  <td>{entry.section}</td>
                  <td>{entry.type}</td>
                  <td>{entry.lifeInDays}</td>
                  <td>{entry.lifeInMonths}</td>
                  <td>{entry.nextSchedule}</td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="5" className="text-center">No cumulative data available</td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ElectricalMaintenance;
