import React, { useState, useEffect } from "react";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
} from "recharts";
import "bootstrap/dist/css/bootstrap.min.css";
import "./Electrical.css";
import Swal from "sweetalert2";  // ✅ For popup alerts

const sections = [
  "TOP_APRON",
  "BOTTOM_APRON",
  "SKF",
  "COMPACT",
  "ELECTRICAL_PANEL",
  "GENERATOR",
  "TRANSFORMER",
];

const Electrical = () => {
  const [selectedDate, setSelectedDate] = useState("");
  const [cumulativeDate, setCumulativeDate] = useState("");
  const [graphData, setGraphData] = useState([]);
  const [data, setData] = useState({});
  const [cumulative, setCumulative] = useState([]);

  // Initialize form data structure
  useEffect(() => {
    const initialData = sections.reduce((acc, section) => {
      acc[section] = {
        type: "",
        lifeInDays: 0,
        lifeInMonths: 0,
        nextSchedule: "",
      };
      return acc;
    }, {});
    setData(initialData);
  }, []);

  // Fetch Graph Data based on the selected date
  const fetchGraphData = async () => {
    if (selectedDate) {
      try {
        const res = await fetch(`http://localhost:5000/maintenance/${selectedDate}`);
        const result = await res.json();

        const formattedData = sections.map((section) => ({
          name: section,
          Days: result[section]?.lifeInDays || 0,
          Months: result[section]?.lifeInMonths || 0,
        }));

        setGraphData(formattedData);
      } catch (err) {
        console.error("Error fetching chart data:", err);
      }
    }
  };

  // Fetch Cumulative Data based on selected date
  const fetchCumulativeData = async () => {
    if (cumulativeDate) {
      try {
        const res = await fetch(`http://localhost:5000/cumulative/${cumulativeDate}`);
        const data = await res.json();

        const formattedData = sections.map((section) => ({
          date: cumulativeDate,
          section,
          type: data[section]?.type || "-",
          lifeInDays: data[section]?.lifeInDays || 0,
          lifeInMonths: data[section]?.lifeInMonths || 0,
          nextSchedule: data[section]?.nextSchedule || "-",
        }));

        setCumulative(formattedData);
      } catch (err) {
        console.error("Error fetching cumulative data:", err);
      }
    }
  };

  useEffect(() => {
    fetchGraphData();
    fetchCumulativeData();
  }, [selectedDate, cumulativeDate]);

  // Handle form input changes
  const handleChange = (section, field, value) => {
    setData((prev) => ({
      ...prev,
      [section]: { ...prev[section], [field]: value },
    }));
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    const newData = { date: selectedDate, ...data };

    try {
      const response = await fetch("http://localhost:5000/add-maintenance", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(newData),
      });

      const result = await response.json();

      if (response.ok) {
        Swal.fire({
          title: "Success!",
          text: "Data successfully entered and added to backend!",
          icon: "success",
          timer: 2000,
        });

        // ✅ Refresh cumulative table and graph data
        fetchGraphData();
        fetchCumulativeData();
        
        // Reset form
        setSelectedDate("");
      } else {
        throw new Error(result.message);
      }
    } catch (err) {
      Swal.fire({
        title: "Error!",
        text: "Failed to add data. Please try again.",
        icon: "error",
        timer: 2000,
      });
      console.error("Error submitting data:", err);
    }
  };

  return (
    <div className="container-fluid p-5">
      
      {/* ✅ Graph Visualization */}
      <div className="row mb-5">
        <div className="col-md-12">
          <h3 className="text-primary font-weight-bold">
            Electrical Maintenance Data - {selectedDate || "Select Date"}
          </h3>
          <ResponsiveContainer width="100%" height={400}>
            <LineChart data={graphData}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="name" />
              <YAxis />
              <Tooltip />
              <Legend />
              <Line type="monotone" dataKey="Days" stroke="#4e73df" strokeWidth={3} />
              <Line type="monotone" dataKey="Months" stroke="#1cc88a" strokeWidth={3} />
            </LineChart>
          </ResponsiveContainer>
        </div>
      </div>

      <div className="row">
        
        {/* ✅ Form Section */}
        <div className="col-md-6">
          <h2 className="text-primary font-weight-bold mb-4">Maintenance Entry</h2>
          <form className="bg-light p-4 rounded shadow-lg" onSubmit={handleSubmit}>
            
            <div className="mb-4">
              <label className="form-label">Select Date:</label>
              <input
                type="date"
                value={selectedDate}
                onChange={(e) => setSelectedDate(e.target.value)}
                className="form-control shadow-sm"
                required
              />
            </div>

            <h3 className="text-secondary mb-3">Sections</h3>

            {sections.map((section) => (
              <div key={section} className="card mb-3 p-3 border-0 shadow-sm rounded">
                <h5 className="text-success font-weight-bold">{section}</h5>

                <div className="mb-3">
                  <label className="form-label">Type:</label>
                  <input
                    type="text"
                    value={data[section]?.type || ""}
                    onChange={(e) => handleChange(section, "type", e.target.value)}
                    className="form-control shadow-sm"
                    required
                  />
                </div>

                <div className="mb-3">
                  <label className="form-label">Life in Days:</label>
                  <input
                    type="number"
                    value={data[section]?.lifeInDays || 0}
                    onChange={(e) => handleChange(section, "lifeInDays", e.target.value)}
                    className="form-control shadow-sm"
                    required
                  />
                </div>

                <div className="mb-3">
                  <label className="form-label">Life in Months:</label>
                  <input
                    type="number"
                    value={data[section]?.lifeInMonths || 0}
                    onChange={(e) => handleChange(section, "lifeInMonths", e.target.value)}
                    className="form-control shadow-sm"
                    required
                  />
                </div>

                <div className="mb-3">
                  <label className="form-label">Next Schedule:</label>
                  <input
                    type="text"
                    value={data[section]?.nextSchedule || ""}
                    onChange={(e) => handleChange(section, "nextSchedule", e.target.value)}
                    className="form-control shadow-sm"
                    required
                  />
                </div>
              </div>
            ))}

            <button type="submit" className="btn btn-primary btn-block py-2 mt-4 shadow-sm">
              Submit
            </button>
          </form>
        </div>

        {/* ✅ Cumulative Data Section */}
        <div className="col-md-6">
          <h2 className="text-primary font-weight-bold mb-4">Cumulative Data</h2>

          <div className="mb-4">
            <label className="form-label">Select Cumulative Date:</label>
            <input
              type="date"
              value={cumulativeDate}
              onChange={(e) => setCumulativeDate(e.target.value)}
              className="form-control shadow-sm"
              required
            />
          </div>

          <table className="table table-bordered rounded shadow-sm">
            <thead className="table-dark">
              <tr>
                <th>Section</th>
                <th>Type</th>
                <th>Days</th>
                <th>Months</th>
                <th>Next Schedule</th>
              </tr>
            </thead>
            <tbody>
              {cumulative.map((item, index) => (
                <tr key={index}>
                  <td>{item.section}</td>
                  <td>{item.type}</td>
                  <td>{item.lifeInDays}</td>
                  <td>{item.lifeInMonths}</td>
                  <td>{item.nextSchedule}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

      </div>
    </div>
  );
};

export default Electrical;
