import React, { useEffect, useState } from "react";
import {
  LineChart,
  Line,
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
} from "recharts";
import Sidebar from "../components/Sidebar";
import "./Admin.css";

const Admin = () => {
  const [productionData, setProductionData] = useState([]);
  const [electricalData, setElectricalData] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const prodRes = await fetch(`${process.env.REACT_APP_API_URL}/api/admin/production-summary`);
        const prodData = await prodRes.json();
        setProductionData(prodData);

        const elecRes = await fetch(`${process.env.REACT_APP_API_URL}/api/admin/electrical-summary`);
        const elecData = await elecRes.json();
        setElectricalData(elecData);
      } catch (err) {
        console.error("Error fetching admin data:", err);
      }
    };

    fetchData();
  }, []);

  return (
    <div className="admin-container">
      <Sidebar />
      <div className="admin-content">
        <h1 className="admin-title">Admin Dashboard</h1>

        {/* Production Chart */}
        <div className="admin-card">
          <h2>Production Data</h2>
          <ResponsiveContainer width="100%" height={300}>
            <LineChart data={productionData}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="date" stroke="#fff" />
              <YAxis stroke="#fff" />
              <Tooltip />
              <Legend />
              <Line type="monotone" dataKey="quantity" stroke="#38bdf8" />
              <Line type="monotone" dataKey="downtime" stroke="#f472b6" />
            </LineChart>
          </ResponsiveContainer>
        </div>

        {/* Electrical Chart */}
        <div className="admin-card">
          <h2>Electrical Maintenance</h2>
          <ResponsiveContainer width="100%" height={300}>
            <BarChart data={electricalData}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="date" stroke="#fff" />
              <YAxis stroke="#fff" />
              <Tooltip />
              <Legend />
              <Bar dataKey="breakdowns" fill="#f87171" />
              <Bar dataKey="repaired" fill="#34d399" />
            </BarChart>
          </ResponsiveContainer>
        </div>
      </div>
    </div>
  );
};

export default Admin;
