import React, { useState, useEffect } from "react";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
} from "recharts";
import "bootstrap/dist/css/bootstrap.min.css";
import "./Electrical.css";

const sections = [
  "TOP_APRON",
  "BOTTOM_APRON",
  "SKF",
  "COMPACT",
  "ELECTRICAL_PANEL",
  "GENERATOR",
  "TRANSFORMER",
];

const Maintenance = () => {
  const [selectedDate, setSelectedDate] = useState("");
  const [graphData, setGraphData] = useState([]);
  const [data, setData] = useState({});
  const [cumulative, setCumulative] = useState({});

  // Initialize form data structure
  useEffect(() => {
    const initialData = sections.reduce((acc, section) => {
      acc[section] = {
        type: "",
        lifeInDays: 0,
        lifeInMonths: 0,
        nextSchedule: "",
      };
      return acc;
    }, {});
    setData(initialData);
  }, []);

  // Fetch cumulative data on page load and after form submission
  const fetchCumulativeData = () => {
    fetch(`http://localhost:5000/cumulative`)
      .then((res) => res.json())
      .then((data) => setCumulative(data))
      .catch((err) => console.error("Error fetching cumulative data:", err));
  };

  useEffect(() => {
    fetchCumulativeData();
  }, []);

  // Fetch chart data based on the selected date
  useEffect(() => {
    if (selectedDate) {
      fetch(`http://localhost:5000/maintenance/${selectedDate}`)
        .then((res) => res.json())
        .then((data) => {
          setGraphData(
            sections.map((section) => ({
              name: section,
              Days: data[section]?.lifeInDays || 0,
              Months: data[section]?.lifeInMonths || 0,
            }))
          );
        })
        .catch((err) => console.error("Error fetching chart data:", err));
    }
  }, [selectedDate]);

  // Handle form input changes
  const handleChange = (section, field, value) => {
    setData((prev) => ({
      ...prev,
      [section]: { ...prev[section], [field]: value },
    }));
  };

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    const newData = { date: selectedDate, ...data };

    fetch("http://localhost:5000/add-maintenance", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(newData),
    })
      .then((res) => res.json())
      .then((response) => {
        alert(response.message);

        // Reset the form and refresh cumulative data immediately
        setSelectedDate("");
        fetchCumulativeData();
      })
      .catch((err) => console.error("Error submitting data:", err));
  };

  return (
    <div className="container-fluid p-5">
      <div className="row mb-5">
        {/* Line Chart */}
        <div className="col-md-12 mb-4">
          <h3 className="text-primary font-weight-bold mb-3">
            Electrical Maintenance Data - {selectedDate || "Select Date"}
          </h3>
          <ResponsiveContainer width="100%" height={400}>
            <LineChart data={graphData}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="name" />
              <YAxis />
              <Tooltip />
              <Legend />
              <Line type="monotone" dataKey="Days" stroke="#4e73df" strokeWidth={3} />
              <Line type="monotone" dataKey="Months" stroke="#1cc88a" strokeWidth={3} />
            </LineChart>
          </ResponsiveContainer>
        </div>
      </div>

      <div className="row">
        {/* Form Section */}
        <div className="col-md-6">
          <h2 className="text-primary font-weight-bold mb-4">Maintenance Entry</h2>
          <form className="bg-light p-4 rounded shadow-lg" onSubmit={handleSubmit}>
            <div className="mb-4">
              <label className="form-label">Select Date:</label>
              <input
                type="date"
                value={selectedDate}
                onChange={(e) => setSelectedDate(e.target.value)}
                className="form-control shadow-sm"
                required
              />
            </div>

            <h3 className="text-secondary mb-3">Sections</h3>
            {sections.map((section) => (
              <div key={section} className="card mb-3 p-3 border-0 shadow-sm rounded">
                <h5 className="text-success font-weight-bold">{section}</h5>
                <div className="mb-3">
                  <label className="form-label">Type:</label>
                  <input
                    type="text"
                    value={data[section]?.type || ""}
                    onChange={(e) => handleChange(section, "type", e.target.value)}
                    className="form-control shadow-sm"
                    required
                  />
                </div>
                <div className="mb-3">
                  <label className="form-label">Life in Days:</label>
                  <input
                    type="number"
                    value={data[section]?.lifeInDays || 0}
                    onChange={(e) => handleChange(section, "lifeInDays", e.target.value)}
                    className="form-control shadow-sm"
                    required
                  />
                </div>
                <div className="mb-3">
                  <label className="form-label">Life in Months:</label>
                  <input
                    type="number"
                    value={data[section]?.lifeInMonths || 0}
                    onChange={(e) => handleChange(section, "lifeInMonths", e.target.value)}
                    className="form-control shadow-sm"
                    required
                  />
                </div>
                <div className="mb-3">
                  <label className="form-label">Next Schedule:</label>
                  <input
                    type="text"
                    value={data[section]?.nextSchedule || ""}
                    onChange={(e) => handleChange(section, "nextSchedule", e.target.value)}
                    className="form-control shadow-sm"
                    required
                  />
                </div>
              </div>
            ))}
            <button type="submit" className="btn btn-primary btn-block py-2 mt-4 shadow-sm">
              Submit
            </button>
          </form>
        </div>

        {/* Cumulative Data Section */}
        <div className="col-md-6">
          <h2 className="text-primary font-weight-bold mb-4">Cumulative Data</h2>

          <h3 className="mt-4 text-secondary">All Cumulative Data</h3>
          <table className="table table-bordered mt-3 rounded shadow-sm">
            <thead className="table-dark">
              <tr>
                <th>Section</th>
                <th>Type</th>
                <th>Days</th>
                <th>Months</th>
                <th>Next Schedule</th>
              </tr>
            </thead>
            <tbody>
              {sections.map((section) => (
                <tr key={section}>
                  <td>{section}</td>
                  <td>{cumulative[section]?.type || "-"}</td>
                  <td>{cumulative[section]?.lifeInDays || 0}</td>
                  <td>{cumulative[section]?.lifeInMonths || 0}</td>
                  <td>{cumulative[section]?.nextSchedule || "-"}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default Maintenance;
