import React, { useState, useEffect } from "react";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
} from "recharts";
import "bootstrap/dist/css/bootstrap.min.css";
import "./Production.css"; // Custom CSS for styling
import Swal from "sweetalert2"; // ✅ For popup messages

const departments = [
  "MIXING",
  "BR_CDG",
  "PREDRG",
  "LH15",
  "COMBER",
  "DRG",
  "SMX",
  "SPG",
  "ACWDG",
  "PACKBAGS",
];

const Production = () => {
  const [leftDate, setLeftDate] = useState("");
  const [rightDate, setRightDate] = useState("");
  const [cumulativeDate, setCumulativeDate] = useState("");
  //const [leftGraphData, setLeftGraphData] = useState([]);
  //const [rightGraphData, setRightGraphData] = useState([]);
  const [graphData, setGraphData] = useState([]);
  const [data, setData] = useState({});
  const [cumulative, setCumulative] = useState([]);

  useEffect(() => {
    const initialData = departments.reduce((acc, dept) => {
      acc[dept] = { ondate_prod: 0, ondate_hands: 0 };
      return acc;
    }, {});
    setData(initialData);
  }, []);

  const fetchGraphData = async (date, setGraphData) => {
    if (date) {
      try {
        const res = await fetch(`http://localhost:5000/production/selected-date/${date}`);
        const result = await res.json();

        const formattedData = departments.map((dept) => ({
          name: dept,
          Production: result[dept]?.ondate_prod || 0,
          Hands: result[dept]?.ondate_hands || 0,
        }));

        setGraphData(formattedData);
      } catch (err) {
        console.error("Error fetching graph data:", err);
      }
    }
  };

  const fetchCumulativeData = useCallback(async () => {
    if (cumulativeDate) {
      const month = cumulativeDate.slice(0, 7);
      try {
        const res = await fetch(`http://localhost:5000/production/cumulative/${month}`);
        const result = await res.json();
  
        const formattedData = departments.map((dept) => ({
          date: month,
          department: dept,
          prod: result[dept]?.prod || 0,
          hands: result[dept]?.hands || 0,
        }));
  
        setCumulative(formattedData);
        setGraphData(formattedData);
      } catch (err) {
        console.error("Error fetching cumulative data:", err);
      }
    }
  }, [cumulativeDate]);
  
  useEffect(() => {
    fetchGraphData(leftDate, setGraphData); // Update this if you're using only graphData
    fetchCumulativeData();
  }, [leftDate, rightDate, cumulativeDate, fetchCumulativeData]); // ✅ Add function
  


  const handleChange = (dept, field, value) => {
    setData((prev) => ({
      ...prev,
      [dept]: { ...prev[dept], [field]: Math.max(0, Number(value)) },
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const newData = { date: leftDate, ...data };

    try {
      const response = await fetch("http://localhost:5000/add", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(newData),
      });

      const result = await response.json();

      if (response.ok) {
        Swal.fire({
          title: "Success!",
          text: "Data successfully entered!",
          icon: "success",
          timer: 2000,
        });

        fetchCumulativeData();
        setLeftDate("");
      } else {
        throw new Error(result.message);
      }
    } catch (err) {
      console.error("Error submitting data:", err);
    }
  };

  return (
    <div className="container-fluid p-5">
      <div className="row mb-5">
        {/* ✅ Production Entry Form */}
        <div className="col-md-6">
          <h3 className="text-primary font-weight-bold">Production Entry</h3>
          <form onSubmit={handleSubmit} className="bg-light p-4 rounded shadow-lg">
            <div className="mb-3">
              <label>Select Date:</label>
              <input
                type="date"
                value={leftDate}
                onChange={(e) => setLeftDate(e.target.value)}
                className="form-control"
                required
              />
            </div>

            {departments.map((dept) => (
              <div key={dept} className="mb-3">
                <h5>{dept}</h5>
                <input
                  type="number"
                  value={data[dept]?.ondate_prod || 0}
                  onChange={(e) => handleChange(dept, "ondate_prod", e.target.value)}
                  className="form-control mb-2"
                  placeholder="Production"
                  required
                />
                <input
                  type="number"
                  value={data[dept]?.ondate_hands || 0}
                  onChange={(e) => handleChange(dept, "ondate_hands", e.target.value)}
                  className="form-control"
                  placeholder="Hands"
                  required
                />
              </div>
            ))}
            <button type="submit" className="btn btn-success mt-3">Submit</button>
          </form>
        </div>

        {/* ✅ Cumulative Data Table with Date Picker */}
        <div className="col-md-6">
          <h3 className="text-primary">Cumulative Data</h3>
          
          <div className="mb-3">
            <label>Select Cumulative Date:</label>
            <input
              type="date"
              value={cumulativeDate}
              onChange={(e) => setCumulativeDate(e.target.value)}
              className="form-control"
              required
            />
          </div>

          <table className="table table-bordered mt-3">
            <thead className="table-dark">
              <tr>
                <th>Date</th>
                <th>Department</th>
                <th>PRODN</th>
                <th>HANDS</th>
              </tr>
            </thead>
            <tbody>
              {cumulative.map((item, index) => (
                <tr key={index}>
                  <td>{item.date}</td>
                  <td>{item.department}</td>
                  <td>{item.prod}</td>
                  <td>{item.hands}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {/* ✅ Graph Visualization */}
      <div className="row mt-5">
        <div className="col-12">
          <h3 className="text-primary text-center">Graph Visualization</h3>
          <ResponsiveContainer width="100%" height={400}>
            <LineChart data={graphData}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="department" />
              <YAxis />
              <Tooltip />
              <Legend />
              <Line type="monotone" dataKey="prod" stroke="#8884d8" name="Production" />
              <Line type="monotone" dataKey="hands" stroke="#82ca9d" name="Hands" />
            </LineChart>
          </ResponsiveContainer>
        </div>
      </div>
    </div>
  );
};

export default Production;
